import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-cards',
  templateUrl: './cards.component.html',
  styleUrls: ['./cards.component.css']
})
export class CardsComponent {

  @Input() nombre : string = '';
  @Input() imagen : string = '';

  constructor() {}


  ngOnInit(): void {
  }
}
<div class="card">
    <figure>
        <img src={{imagen}}>
    </figure>
    <div class="contenido">
        <h3>{{nombre}}</h3>
    </div>
</div>
