import { Component } from '@angular/core';
import { MoviesService } from 'src/app/services/movies.service';
import { MovieSerie } from 'src/app/shared/interfaces/movieSerie.interface';
import { Trending } from 'src/app/shared/interfaces/Trending.interface';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css'],
  providers: [MoviesService]
})


export class InicioComponent {
  
  movies_series : any = {}; 


  selected: string = 'Todos';

  paraBuscar: string = '';

  constructor(
    private _moviesService: MoviesService
  ) {}
  
  ngOnInit() : void{
    this.getTrendingAll();
  }

  getTrendingAll() {
    this._moviesService.getTrending().subscribe({
      next: (data) => {
        this.movies_series = data;
        for (const element of this.movies_series.results) {
          element.poster_path = 'https://www.themoviedb.org/t/p/w220_and_h330_face/' + element.poster_path;
        }
      },
      error: (error) => {
        console.log(error);
      }
    })
  }
  
  cambiarCategoria(value: string){
    this.selected = value;
  }

  buscar(value: string) {
    console.log(this.paraBuscar);
  }



}
<section>
    <div class="container">
        <div class="hero">
            <h1>PelisUP!</h1>
            <h2>Plataforma para ver peliculas y series üòÅ</h2>
            <form class="d-flex" role="search">
                <input class="form-control me-2" type="search" placeholder=" üîçBuscar serie o pel√≠cula" aria-label="Search" [(ngModel)]="paraBuscar" (ngModelChange)="buscar(paraBuscar)"[ngModelOptions]="{standalone: true}">
            </form>
            <button type="button" class="btn btn-lg botonTab" (click)="cambiarCategoria('Todos')" [ngClass]="selected == 'Todos' ? 'button-Selected' : 'button-NoSelected' ">Todos</button>
            <button type="button" class="btn btn-lg botonTab" (click)="cambiarCategoria('movie')" [ngClass]="selected == 'movie' ? 'button-Selected' : 'button-NoSelected' ">Pel√≠culas</button>
            <button type="button" class="btn btn-lg botonTab" (click)="cambiarCategoria('tv')" [ngClass]="selected == 'tv' ? 'button-Selected' : 'button-NoSelected' ">Series</button>
        </div>
    </div>
</section>

<section class="wrapperCards" >
    <div class="container">
        <div class="row">
            <div class="col-4" *ngFor="let movie of movies_series.results">
                <div class="card" *ngIf="selected == movie.media_type || selected == 'Todos' ">
                    <figure>
                       <img src={{movie.poster_path}}>
                    </figure>
                    <div class="contenido">
                        <h3>{{movie.title || movie.original_name}}</h3>
                    </div>
                </div>   
            </div> 
        </div>
    </div>
</section>
