import { Component } from '@angular/core';

@Component({
  selector: 'app-peliculas',
  templateUrl: './peliculas.component.html',
  styleUrls: ['./peliculas.component.css']
})
export class InicioComponent {
  
  movies_series : any = {}; 


  selected: string = 'Todos';

  paraBuscar: string = '';

  constructor(
    private _moviesService: MoviesService
  ) {}
  
  ngOnInit() : void{
    this.getTrendingAll();
  }

  getTrendingAll() {
    this._moviesService.getTrending().subscribe({
      next: (data) => {
        this.movies_series = data;
        for (const element of this.movies_series.results) {
          element.poster_path = 'https://www.themoviedb.org/t/p/w220_and_h330_face/' + element.poster_path;
        }
      },
      error: (error) => {
        console.log(error);
      }
    })
  }

  getTv() {
    this._moviesService.getSeries().subscribe({
      next: (data) => {
        this.movies_series = data;
        for (const element of this.movies_series.results) {
          element.poster_path = 'https://www.themoviedb.org/t/p/w220_and_h330_face/' + element.poster_path;
        }
      },
      error: (error) => {
        console.log(error);
      }
    })
  }

  getMovies() {
    this._moviesService.getMovies().subscribe({
      next: (data) => {
        this.movies_series = data;
        for (const element of this.movies_series.results) {
          element.poster_path = 'https://www.themoviedb.org/t/p/w220_and_h330_face/' + element.poster_path;
        }
      },
      error: (error) => {
        console.log(error);
      }
    })
  }
  
  cambiarCategoria(value: string){
    this.selected = value;
    if (value == 'Tv') {
      this.getTv();
    } else if (value == 'Movie') {
      this.getMovies();
    } else {
      this.getTrendingAll
    }
  }

  buscar(value: string) {
    console.log(this.paraBuscar);
  }



}
